let scrollBtn = document.getElementById("btn-arv"),
    arv = 80;

let fixscroll = () => {
    if (window.pageYOffset >= arv) {
        scrollBtn.style.opacity = "1";
    } else {
        scrollBtn.style.opacity = ".1";
    }

}


